mapper.update:
UPDATE todo SET
	title = #{instance.title}, details = #{instance.details}, finished = #{instance.finished}, last_updated_at = #{instance.lastUpdatedAt}, last_updated_by = #{instance.lastUpdatedBy}
WHERE
	id = #{instance.id}

mapper.select:
SELECT id, title, details, finished, created_at, created_by, last_updated_at, last_updated_by FROM todo
#{idValue}
ORDER BY id
